                var scroll = $(window).scrollTop();
                if (scroll > 100) {
                    $(".minicart-wrapper").removeClass("sticky-off").addClass("sticky-on");
                } else {
                    $(".minicart-wrapper").removeClass("sticky-on").addClass("sticky-off");
