    require(['jquery','mmenu','mmenu.pollyfills'], function($,mmenu) {
        $(function(){
            const menu = new Mmenu( "#pw-menu", {
                extensions: {
                    all: ["theme-white", "pagedim-black","border-none","multiline"],
                    "(max-width: 767px)": ["fx-menu-slide"]
                },
                "setSelected": {
                    "current": "detect",
                    "hover": true,
                    "parent": true
                },
                scrollBugFix: {
                    fix: true
                },
                navbars		: [{
                    content : ["", "close"]
                }]}, {
                // configuration
                offCanvas: {
                    page: {
                        selector: ".page-wrapper",
                        blockUI: true,
                        moveBackground: false
                    }
                }
            });

            // Get the API
            const api = menu.API;

            $('body').on( 'click',
                'a.customer-login-link',
                function(e) {
                    e.preventDefault();
                    api.close();
                }
            );

            $('body').on( 'click',
                'a.account-quick-order-link',
                function(e) {
                    console.log('closeApi');
                    e.preventDefault();
                    api.close();
                    $('.pp-collapse-toggle.quick-order-btn').trigger('click');
                }
            );

            $('body').on( 'click',
                'a.quick-order-btn-link',
                function(e) {
                    e.preventDefault();
                    api.close();
                    $('.pp-collapse-toggle.quick-order-btn').trigger('click');
                }
            );
        });
    });

